package day2;

public class ShiftEx1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 12;
		
		int leftShift = num1 << 2;
		int rightShift = num1 >> 2;
		
		System.out.println(num1 +" << 2 = " + leftShift );
		System.out.println(num1 +" >> 2 = " + rightShift );
	}
	

}
